mapper.update:
UPDATE 
           POST_TBL A
       SET A.VIEW_CNT = (SELECT B.VIEW_CNT
                              FROM POST_TBL B
                             WHERE B.POST_NO = #{ postNo }
                           ) + 1
     WHERE A.POST_NO = #{ postNo }

