mapper.select:
SELECT C.RNUM , C.SD_CODE , C.SD_NAME , C.PRICE , C.VOLUME , C.SD_IMG , C.CATEGORY , C.DISCOUNT_RATE , C.MEM_CODE , C.STORE_NAME FROM (SELECT ROWNUM RNUM , B.SD_CODE , B.SD_NAME , B.PRICE , B.VOLUME , B.SD_IMG , B.CATEGORY , B.DISCOUNT_RATE , B.MEM_CODE , B.STORE_NAME FROM (SELECT A.SD_CODE , A.SD_NAME , A.PRICE , A.VOLUME , A.SD_IMG , A.CATEGORY , A.DISCOUNT_RATE , A.MEM_CODE , S.STORE_NAME FROM PRODUCT_TBL A LEFT JOIN MEMBER_TBL M ON(A.MEM_CODE = M.MEM_CODE) LEFT JOIN STORE_TBL S ON(A.MEM_CODE = S.MEM_CODE)
M.IS_DELETED = 'N' AND A.ORDERABLE_STATUS = 'Y' AND A.IS_DELETED = 'N'
AND A.SD_NAME LIKE '%' || #{ searchValue } || '%'
AND A.RECOMMEND_YN = 'Y'
AND A.CATEGORY_CODE = #{ searchCondition }
AND A.DISCOUNT_RATE > 0
)B
WHERE ROWNUM <= #{ endRow } )C
WHERE C.RNUM >= #{ startRow }

SELECT C.RNUM , C.MEM_CODE , C.RV_IMG , C.RV_CONTENT , C.RV_GRADE , C.RV_CODE , C.MEM_ID , C.SD_NAME FROM (SELECT ROWNUM RNUM , B.MEM_CODE , B.RV_IMG , B.RV_CONTENT , B.RV_GRADE , B.RV_CODE , B.MEM_ID , B.SD_NAME FROM (SELECT A.MEM_CODE , A.RV_IMG , A.RV_CONTENT , A.RV_GRADE , A.RV_CODE , A.MEM_ID , P.SD_NAME FROM REVIEW_TBL A LEFT JOIN ORDER_INFO_TBL O ON(A.ORDER_CODE = O.ORDER_CODE) JOIN PRODUCT_TBL P ON(O.SD_CODE = P.SD_CODE)
A.IS_DELETED = 'N'
AND P.MEM_CODE = #{ searchCondition }
)B
WHERE ROWNUM <= #{ endRow } )C
WHERE C.RNUM >= #{ startRow }

 
